def check_hamming_code(bits):
    n = len(bits)
    res = 0

    # Перевіряємо контрольні біти
    for i in range(n):
        if 2**i >= n:
            break
        count = 0
        for j in range(1, n + 1):
            if j & (2**i):
                count += int(bits[-j])
        # XOR контрольних бітів
        res |= (count % 2) << i

    # Повертаємо позицію помилки
    return res

# Ваша послідовність бітів
bits = ''
error_pos = check_hamming_code(bits)

if error_pos:
    print(f"Помилка знайдена на позиції: {error_pos}")
else:
    print("Контрольні біти правильні.")